mapper.select:

SELECT *
FROM (
SELECT ROWNUM RN, A.*
FROM (
SELECT * FROM MEMBER ORDER BY ID ASC
) A
)
WHERE RN BETWEEN #{start} AND #{end}

SELECT * FROM MEMBER
WHERE ID = #{id}

	AND PASSWORD = #{password}

SELECT *
FROM (
SELECT ROWNUM RN, A.*
FROM (
SELECT * FROM MEMBER
WHERE BLACK = 1
) A
)
WHERE RN BETWEEN #{start} AND #{end}

 
	
		SELECT *
		FROM (
		SELECT ROWNUM RN, A.*
		FROM (
		SELECT * FROM MEMBER WHERE DELAYCOUNT >= 2 AND BLACK = 0
		) A
		)
		WHERE RN BETWEEN #{start} AND #{end}

mapper.update:
UPDATE MEMBER SET
BLACK = 1
WHERE ID = #{id}

mapper.select:
select id from member
where
name=#{name}
and tel=#{tel}

		select password from member
		where
		id=#{id}
		and tel=#{tel}
